			jestExpect(ReactNoop).toMatchRenderedOutput("1")
		end)

		act(function()
			setCounter(2)
		end)
		jestExpect(Scheduler).toHaveYielded({
			"Render: 1",
			"Effect: 2",
			"Reducer: 2",
			"Reducer: 2",
			"Render: 2",
		})

		jestExpect(ReactNoop).toMatchRenderedOutput("2")
	end
)

