		function()
			local instance
			local Foo = React.Component:extend("Foo")
			function Foo:init()
				self.state = {}
			end

			function Foo:componentDidMount()
				Scheduler.unstable_yieldValue("componentDidMount")
			end

			function Foo:componentDidUpdate()
				Scheduler.unstable_yieldValue("componentDidUpdate")
			end

			function Foo:render()
