			function Bar:render()
				return React.createElement(Text, {
					text = "Bar",
				})
			end

			return fakeImport(React.forwardRef(function(props, ref)
				Scheduler.unstable_yieldValue("forwardRef")

				return React.createElement(Bar, { ref = ref })
